import React from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from '../Components/Navbar';
import Cartlist from '../Components/Cartlist';
import './Cart.css';

function Cart(props) {
  const navigate = useNavigate();
  function openProduct(prod) {
    navigate("/product", { state: prod });
  }

  let CartData = props.Showlist.filter((item) => item.inCart === true);

  console.log(CartData, 'Filtered cart data');

  return (
    <>
      <Navbar />
      <div className="CartBody">
        <div 
        className="cartBodyListBody"
            key={prod.id}
            onClick={() => openProduct(prod)}
        >
          <h1 className="CartTitleis">Cart Items</h1>
          {CartData.map((procar) => (
            <Cartlist
              key={procar.id}
              imgSrc={procar.thumbnail}
              title={procar.productName}
              price={procar.price}
              Qty={procar.stock}
              rate={procar.rate}
              ppv={procar.ppv}
              unit={procar.unit}
            />
          ))}
        </div>
      </div>
    </>
  );
}

export default Cart;
