import React from 'react'
import './Wishlist.css'
import axios from 'axios'
import Navbar from '../Components/Navbar'
import Cartlist from '../Components/Cartlist'

function Wishlist(props) {
    let MyWishlist = props.Showlist
    console.log(MyWishlist, "WishlistData")

    const removeFromWishlist = async (productId) => {
        try {
            const response = await axios.get('http://localhost:5006/users/1');
            const updatedWishlist = response.data.wishList.filter(id => id !== productId);
            await axios.patch('http://localhost:5006/users/1', { wishList: updatedWishlist });
            alert('Removed from Wishlist');
        } catch (error) {
            console.error('Error removing from wishlist:', error);
        }
    };

  return (
    <>
    <Navbar/>
    <div className="wishBody">
        <div className="wishBodyListBody">
            <h1 className="WishTitleis">Wishlist</h1>
            {MyWishlist.map((prowish)=> (
          <>
          <Cartlist
                                  key={prowish.id}

              imgSrc={prowish.thumbnail}
              title={prowish.productName}
              price={prowish.price}
              Qty={prowish.stock}
              rate={prowish.rate}
              ppv={prowish.ppv}
              unit={prowish.unit}
              />
              </>
            ))}
        </div>
    </div>
    </>
  )
}

export default Wishlist
