import React, { useState } from 'react';
import "../Pages/Admin.css"

function AddProduct() {

    const [selectedCategory, setSelectedCategory] = useState('All');
    const [productName, setproductName] = useState ('')
    const [brand, setbrand] = useState ('')
    const [Category, setCategory] = useState ('')
    const [subCategory, setsubCategory] = useState ('')
    const [specification, setspecification] = useState ('')
    const [unit, setunit] = useState ('')
    const [tags, settags] = useState ('')
    const [description, setdescription] = useState ('')
    const [price, setprice] = useState ('')
    const [compare, setcompare] = useState ('')
    const [discount, setdiscount] = useState ('')
    const [thumbnail, setthumbnail] = useState ('')
    const [img1, setimg1] = useState ('')
    const [img2, setimg2] = useState ('')
    const [img3, setimg3] = useState ('')

    function submit(e) {
        e.preventDefault();
        console.log({ productName});

        let value = { name, number, email, pwd };
        postData(value);
        empty();
        }

    function clear() {
        setproductName("");
        setbrand("");
        setCategory("");
        setsubCategory("");
        setspecification("");
        setunit("");
        settags("");
        setdescription("");
        setprice("");
        setcompare("");
        setdiscount("");
        setthumbnail("");
        setimg1("");
        setimg2("");
        setimg3("");
    }

    const subCategories = {
        Electronics: ['Phones', 'Laptops', 'Cameras', 'Television'],
        Fashion: ['Men', 'Women', 'Kids'],
        Furniture: ['Chairs', 'Tables', 'Beds', 'Sofas'],
        Appliances: ['Refrigerators', 'Washing Machines', 'Microwaves', 'Air Conditioners'],
        Beauty: ['Makeup', 'Skincare', 'Haircare', 'Fragrances'],
        Food: ['Groceries', 'Snacks', 'Beverages', 'Dairy'],
    };

    const handleCategoryChange = (event) => {
        setSelectedCategory(event.target.value);
    };

    
    return (
        <>
            <main className="addProductMain">
                <form action="">
                    <table className='addProductsMainTable'>
                        <thead>
                            <tr>
                                <th colspan="2"><h1>Produt Information</h1></th><th></th>
                            </tr>
                        </thead>
                        <tbody>
                            <tr>
                                <td><label htmlFor="">Product Name</label></td>
                                <td><input type="text" placeholder='Product Name'  value={productName} onChange={(e) => setId(e.target.productName)}/></td>
                            </tr>
                            <tr>
                                <td><label htmlFor="">Brand</label></td>
                                <td><input type="text" placeholder='Brand Name' value={brand} onChange={(e) => setId(e.target.brand)} /></td>
                            </tr>
                            <tr>
                                <td><label htmlFor="Category">Category</label></td>
                                <td>
                                    <select name="Category" id="Category" value={selectedCategory} onChange={handleCategoryChange}>                                        <option value="All">All Category</option>
                                        <option value="Electronics">Electronics</option>
                                        <option value="Fashion">Fashion</option>
                                        <option value="Furniture">Furniture</option>
                                        <option value="Appliances">Appliances</option>
                                        <option value="Beauty">Beauty</option>
                                        <option value="Food">Food</option>
                                    </select>
                                </td>
                            </tr>
                            <tr>
                                <td><label htmlFor="SubCategory">Sub Category</label></td>
                                <td>
                                    <select name="subCategory" id="subCategory">
                                        {selectedCategory === 'All' ?
                                            (<option value="N/A">Select a category first</option>) :
                                            (
                                                subCategories[selectedCategory]?.map((subCategory, index) => (
                                                    <option key={index} value={subCategory}>{subCategory}</option>
                                                ))
                                            )}
                                    </select>
                                </td>
                            </tr>
                            <tr>
                                <td><label htmlFor="">Specifications</label></td>
                                <td><input type="text" placeholder='Enter Specifications' /></td>
                            </tr>
                            <tr>
                                <td><label htmlFor="">Unit</label></td>
                                <td>
                                    <select name="Unit" id="productUnit">
                                        <option value="pc">Piece</option>
                                        <option value="kg">Kilogram</option>
                                        <option value="g">Gram</option>
                                        <option value="m">Meter</option>
                                        <option value="cm">Centimeter</option>
                                        <option value="l">Liter</option>
                                        <option value="ml">Milliliter</option>
                                        <option value="pk">Pack</option>
                                        <option value="bx">Box</option>
                                        <option value="dz">Dozen</option>
                                        <option value="set">Set</option>
                                        <option value="pr">Pair</option>
                                    </select>
                                </td>
                            </tr>
                            <tr>
                                <td><label htmlFor="">Tags</label></td>
                                <td><input type="text" placeholder='Input key words for search' /></td>
                            </tr>
                            <tr>
                                <td><label htmlFor="">Description</label></td>
                                <td><textarea type='text' placeholder='Enter Product Description' /></td>
                            </tr>
                            <tr>
                                <td><label htmlFor="">Price</label></td>
                                <td><input type="number" placeholder='Price' /></td>
                            </tr>
                            <tr>
                                <td><label htmlFor="">Compare</label></td>
                                <td><input type="number" placeholder='Compare' /></td>
                            </tr>
                            <tr>
                                <td><label htmlFor="">Discount</label></td>
                                <td><input type="number" placeholder='Discount' /></td>
                            </tr>
                            <tr>
                                <td><label htmlFor="">Thumbnail</label></td>
                                <td><input type='text' placeholder='Please enter main img link' /></td>
                            </tr>
                            <tr>
                                <td><label htmlFor="">Images1</label></td>
                                <td><input type='text' placeholder='Please enter img link' /></td>
                            </tr>
                            <tr>
                                <td><label htmlFor="">Images2</label></td>
                                <td><input type='text' placeholder='Please enter img link' /></td>
                            </tr>
                            <tr>
                                <td><label htmlFor="">Images3</label></td>
                                <td><input type='text' placeholder='Please enter img link' /></td>
                            </tr>
                            <tr>
                                <td><input type="submit" onClick={(e) => submit(e)}/></td>
                                <td><input type="submit" onClick={(e) => clear(e)}/></td>
                            </tr>
                        </tbody>
                    </table>
                </form>
            </main>

        </>
    )
}

export default AddProduct
