// src/Pages/Home.js
import React, { useState, useEffect } from 'react';
import Navbar from '../Components/Navbar';
import Main from '../Components/Main';
import Banner from '../Components/Banner';

function Home(props) {
  const [theme, setTheme] = useState('dark');
  const [searchTerm, setSearchTerm] = useState(''); // Store the search input
  const [filteredProducts, setFilteredProducts] = useState(props.HomeProducts || []); // Filtered data

  const HomeProductData = props.HomeProducts;

  const toggleTheme = () => {
    setTheme((prevTheme) => (prevTheme === 'light' ? 'dark' : 'dark'));
  };

  // Filter products whenever searchTerm or product data changes
  useEffect(() => {
    const results = HomeProductData.filter((product) =>
      product.productName.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredProducts(results);
  }, [searchTerm, HomeProductData]);

  return (
    <div className={`homeBody ${theme}`}>
      <Navbar theme={theme} toggleTheme={toggleTheme} />
      <Banner />

      {/* Search Input */}
      <div style={{ padding: '20px' }}>
        <input
          type="text"
          placeholder="Search products..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{
            width: '100%',
            padding: '10px',
            marginBottom: '20px',
            border: '1px solid #ccc',
            borderRadius: '4px',
          }}
        />
      </div>

      {/* Pass filtered data to Main component */}
      <Main proData={filteredProducts} />
    </div>
  );
}

export default Home;
