import React, { useState, useEffect } from 'react'
import AdminCharts from '../Components/AdminCharts'
import "../Pages/Admin.css"
import AddProduct from './AddProduct';

function ProductReview(props) {
    let adminProductData = props.adminProducts
    const [getProductData, setgetProductData] = useState([]);
    const [isEditing, setIsEditing] = useState(false);
    const [selectedProduct, setSelectedProduct] = useState(null);

    function fetchProductData() {
        fetch("http://localhost:5006/products")
            .then((resp) => resp.json())
            .then((response) => {
                setgetProductData(response);
                console.log(response, "response");
            });
    }

    useEffect(() => {
        fetchProductData();
    }, []);

    function deleteData(id) {
        fetch(`http://localhost:5006/products/${id}`, {
            method: "DELETE",
        }).then(() => {
            fetchProductData(); // Refresh the list after deletion
        });
    }

    function handleEdit(product) {
        setSelectedProduct(product);
        setIsEditing(true); // Open AddProduct UI
    }
    // function updateData (id, productName)
    // fetch ()    

    return (
        <div className="productReviewBody">
            {isEditing && (
                <AddProduct
                    product={selectedProduct} // Pass selected product for editing
                    onClose={() => setIsEditing(false)} // Close AddProduct when done
                />
            )}
            {getProductData.map((prod) => (<div className="clickProductChart">
                <AdminCharts
                    imgSrc={prod.thumbnail}
                    title={prod.name}
                    price={prod.price}
                    desc={prod.description}
                    rate={prod.rate}
                    brand={prod.brand}
                    market={prod.compare}
                    category={prod.category}
                    subCategory={prod.subCategory}
                    stock={prod.stock}
                    sales={prod.sales}
                    deleteOnClick={() => deleteData(prod.id, prod.name)}
                    onEdit={handleEdit} />
            </div >

            ))
            }
        </div>
    )
}

export default ProductReview
