import React, { useState, useEffect } from 'react';
import './Register.css';
import * as Yup from 'yup';
import { Link, Outlet } from 'react-router-dom';

function Register() {
    const [errors, setErrors] = useState({});
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        email: '',
        phone: '',
        password: '',
        confirmPassword: ''
    });
    const [showLogo, setShowLogo] = useState(true);

    const userSchema = Yup.object({
        firstName: Yup.string().required('First name is required'),
        lastName: Yup.string().required('Last name is required'),
        email: Yup.string().email('Invalid Email Format').required('Email is required'),
        phone: Yup.string().matches(/^\d{10}$/, 'Must be 10 digits').required('Phone number is required'),
        password: Yup.string()
            .required('Password is required')
            .min(8, 'Must be 8 characters')
            .matches(/[!@#$%^&*(),.?":{}|<>]/, 'Must contain one symbol')
            .matches(/[0-9]/, 'Must contain at one number')
            .matches(/[A-Z]/, 'Must contain at one uppercase')
            .matches(/[a-z]/, 'Must contain at one lowercase'),
        confirmPassword: Yup.string()
            .oneOf([Yup.ref("password")], 'Passwords must match')
            .required('Confirm password is required'),
    });

    useEffect(() => {
        const timer = setTimeout(() => {
            setShowLogo(false);
        }, 1000);
        return () => clearTimeout(timer);
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            // Validate form data with Yup
            await userSchema.validate(formData, { abortEarly: false });

            // Proceed only if form is valid
            const userData = {
                firstName: formData.firstName,
                lastName: formData.lastName,
                email: formData.email,
                phone: formData.phone,
                password: formData.password,
            };

            const response = await fetch('http://localhost:5006/users', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(userData),
            });

            const result = await response.json();
            if (response.ok) {
                console.log('Registration Successful:', result);
                clear();
            } else {
                console.error('Registration Failed:', result);
            }
        } catch (error) {
            // If validation fails, update errors state
            const newErrors = {};
            error.inner.forEach((err) => {
                newErrors[err.path] = err.message;
            });
            setErrors(newErrors);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const clear = () => {
        setFormData({
            firstName: '',
            lastName: '',
            email: '',
            phone: '',
            password: '',
            confirmPassword: ''
        });
        setErrors({});
    };

    return (
        <div className="RegisterBody">
            {showLogo ? (
                <div className="logoContainer">
                    <img src="../images/Shopin Logo.png" alt="Logo" className="logoImage" />
                </div>
            ) : (
                <div className="registerFormDiv">
                    <Link to="/" className="RegisterPageLogo">
                        <img src="../images/ShopinWhiteLogo.png" alt="Logo" />
                    </Link>
                    <Outlet />
                    <p className="navToLogin">Already a user? <a href="#">Login</a> now</p>
                </div>
            )}
        </div>
    );
}

export default Register;
