import React, { useState } from 'react';
import AdminCharts from '../Components/AdminCharts';
import "../Pages/Admin.css";

function ProductReview({ adminProducts, setEditProduct }) {
    const [isEditing, setIsEditing] = useState(false);

    function handleEdit(product) {
        setIsEditing(true);  // Set editing mode to true
        setEditProduct(product);  // Pass product data to be edited
    }

    return (
        <div className="productReviewBody">
            {adminProducts.map((prod) => (
                <div className="clickProductChart" key={prod.id}>
                    <AdminCharts
                        imgSrc={prod.thumbnail}
                        title={prod.name}
                        price={prod.price}
                        desc={prod.description}
                        rate={prod.rate}
                        brand={prod.brand}
                        market={prod.compare}
                        category={prod.category}
                        subCategory={prod.subCategory}
                        stock={prod.stock}
                        sales={prod.sales}
                        deleteOnClick={() => deleteData(prod.id, prod.name)}
                        onEdit={() => handleEdit(prod)}  // Trigger edit mode
                    />
                </div>
            ))}
        </div>
    );
}

export default ProductReview;
