import React, { useState, useEffect } from 'react';
import './Register.css';
import * as Yup from 'yup';
import { Link, Outlet, useNavigate, Navigate, useLocation } from 'react-router-dom';

function Register() {
    const [errors, setErrors] = useState({});
    const [create, setCreate] = useState(true);
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        email: '',
        phone: '',
        password: '',
        confirmPassword: ''
    });
    const [showLogo, setShowLogo] = useState(true);
    const navigate = useNavigate();
    const location = useLocation();

    const userSchema = Yup.object({
        firstName: Yup.string().required('First name is required'),
        lastName: Yup.string().required('Last name is required'),
        email: Yup.string().email('Invalid Email Format').required('Email is required'),
        phone: Yup.string().matches(/^\d{10}$/, 'Must be 10 digits').required('Phone number is required'),
        password: Yup.string()
            .required('Password is required')
            .min(8, 'Must be 8 characters')
            .matches(/[!@#$%^&*(),.?":{}|<>]/, 'Must contain one symbol')
            .matches(/[0-9]/, 'Must contain at least one number')
            .matches(/[A-Z]/, 'Must contain at least one uppercase')
            .matches(/[a-z]/, 'Must contain at least one lowercase'),
        confirmPassword: Yup.string()
            .oneOf([Yup.ref('password')], 'Passwords must match')
            .required('Confirm password is required'),
    });

    useEffect(() => {
        const timer = setTimeout(() => setShowLogo(false), 1000);
        return () => clearTimeout(timer);
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await userSchema.validate(formData, { abortEarly: false });

            const userData = { ...formData };
            const response = await fetch('http://localhost:5006/users', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(userData),
            });

            const result = await response.json();
            if (response.ok) {
                console.log('Registration Successful:', result);
                clear();
                navigate('/');
            } else {
                console.error('Registration Failed:', result);
            }
        } catch (error) {
            const newErrors = {};
            error.inner.forEach((err) => (newErrors[err.path] = err.message));
            setErrors(newErrors);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const clear = () => setFormData({
        firstName: '', lastName: '', email: '', phone: '', password: '', confirmPassword: ''
    });

    const handlePage = () => setCreate(!create);

    return (
        <div className="RegisterBody">
            {showLogo ? (
                <div className="logoContainer">
                    <img src="../images/Shopin Logo.png" alt="Logo" className="logoImage" />
                </div>
            ) : (
                <div className="registerFormDiv">
                    <Link to="/" className="RegisterPageLogo">
                        <img src="../images/ShopinWhiteLogo.png" alt="Logo" />
                    </Link>
                    <Outlet context={{ formData, errors, handleChange, handleSubmit, clear }} />
                    {create ? (
                        <p className="navToLogin">
                            Already a user? <Link to="login" onClick={handlePage}>Login</Link> now!!
                        </p>
                    ) : (
                        <p className="navToLogin">
                            New to Shopin? <Link to="signup" onClick={handlePage}>Create Account</Link> now!!
                        </p>
                    )}
                </div>
            )}
        </div>
    );
}

export default Register;
