import React, { useState, useEffect } from 'react';
import AdminCharts from '../Components/AdminCharts';
import AddProduct from '../Components/AddProduct'; // Import AddProduct component
import "../Pages/Admin.css";

function ProductReview() {
    const [getProductData, setGetProductData] = useState([]);
    const [isEditing, setIsEditing] = useState(false);
    const [selectedProduct, setSelectedProduct] = useState(null); // State for selected product

    function fetchProductData() {
        fetch("http://localhost:5006/products")
            .then((resp) => resp.json())
            .then((response) => {
                setGetProductData(response);
                console.log(response, "response");
            });
    }

    useEffect(() => {
        fetchProductData();
    }, []);

    function deleteData(id) {
        fetch(`http://localhost:5006/products/${id}`, {
            method: "DELETE",
        }).then(() => {
            fetchProductData(); // Refresh the list after deletion
        });
    }

    function handleEdit(product) {
        setSelectedProduct(product);
        setIsEditing(true); // Open AddProduct UI
    }

    return (
        <div className="productReviewBody">
            {isEditing && (
                <AddProduct
                    product={selectedProduct} // Pass selected product for editing
                    onClose={() => setIsEditing(false)} // Close AddProduct when done
                />
            )}
            {getProductData.map((prod) => (
                <div className="clickProductChart" key={prod.id}>
                    <AdminCharts
                        imgSrc={prod.thumbnail}
                        title={prod.name}
                        price={prod.price}
                        desc={prod.description}
                        rate={prod.rate}
                        brand={prod.brand}
                        market={prod.compare}
                        category={prod.category}
                        subCategory={prod.subCategory}
                        stock={prod.stock}
                        sales={prod.sales}
                        onButtonClick={() => deleteData(prod.id)}
                        onEdit={handleEdit} // Pass the handleEdit function
                    />
                </div>
            ))}
        </div>
    );
}

export default ProductReview;
