import { BrowserRouter, Routes, Route } from 'react-router-dom';
import React, { useState, useEffect } from 'react';
import './App.css';
import Home from './Pages/Home';
import Product from './Pages/Product';
import Admin from './Pages/Admin';
import AddProduct from './Sections/AddProduct';
import Register from './Pages/Register';
import ProductReview from './Sections/ProductReview';
import StarRating from './Components/StarRating';

function App() {
  const [getProductData, setgetProductData] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null); // For editing

  // Fetch products from the server
  const fetchProductData = () => {
    fetch("http://localhost:5006/products")
      .then((resp) => resp.json())
      .then((response) => {
        setgetProductData(response);
      });
  };

  useEffect(() => {
    fetchProductData();
  }, []);

  // Update product
  const updateProduct = (updatedProduct) => {
    fetch(`http://localhost:5006/products/${updatedProduct.id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(updatedProduct),
    })
      .then(() => fetchProductData()) // Re-fetch products after update
      .catch((error) => console.error('Error:', error));
  };

  // Add new product
  const addProduct = (newProduct) => {
    fetch('http://localhost:5006/products', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(newProduct),
    })
      .then(() => fetchProductData()) // Re-fetch products after adding
      .catch((error) => console.error('Error:', error));
  };

  // Delete product
  const deleteProduct = (id) => {
    fetch(`http://localhost:5006/products/${id}`, {
      method: 'DELETE',
    })
      .then(() => fetchProductData()) // Re-fetch products after deletion
      .catch((error) => console.error('Error:', error));
  };

  return (
    <>
      <BrowserRouter>
        <Routes>
          <Route path='/' element={<Home HomeProducts={getProductData} />} />
          <Route path='/product' element={<Product />} />
          <Route path='/register' element={<Register />} />
          <Route path='/star' element={<StarRating />} />
          <Route path='/admin/' element={<Admin />}>
            <Route
              path="addproduct"
              element={
                <AddProduct
                  addProduct={addProduct}
                  updateProduct={updateProduct}
                  selectedProduct={selectedProduct}
                  setSelectedProduct={setSelectedProduct}
                />
              }
            />
            <Route
              path="allproducts"
              element={
                <ProductReview
                  adminProducts={getProductData}
                  deleteProduct={deleteProduct}
                  setSelectedProduct={setSelectedProduct}
                />
              }
            />
          </Route>
        </Routes>
      </BrowserRouter>
    </>
  );
}

export default App;
