// src/Components/PrimaryNav.js
import React from 'react';
import { Link } from 'react-router-dom';

function PrimaryNav({ theme, toggleTheme, onFilter, onSearch }) {
  
  const handleCategoryChange = (e) => {
    const selectedCategory = e.target.value;
    onFilter(selectedCategory); // Call filter function in Home.js
  };

  return (
    <div className="midNavbar">
      <Link className="midNavbarLink" to="/home">
        <div className="midNavLogo">
          <img src="./images/Shopin Logo.png" alt="Click for Home" />
        </div>
      </Link>

      <div className="midNavSearch">
        <div className="category">
          <select 
            name="Category" 
            id="Category" 
            className="selectCategory" 
            onChange={handleCategoryChange} // Handle category change
          >
            <option value="All">All Category</option>
            <option value="Electronics">Electronics</option>
            <option value="Fashion">Fashion</option>
            <option value="Accessories">Accessories</option>
            <option value="Appliances">Appliances</option>
            <option value="Beauty">Beauty</option>
            <option value="Food">Food</option>
          </select>
        </div>

        <input 
          type="text" 
          placeholder="Search on Shopin..." 
          className="searchInput" 
          onChange={(e) => onSearch(e.target.value)} // Handle search input
        />
      </div>
    </div>
  );
}

export default PrimaryNav;
