// src/Pages/Home.js
import React, { useState, useEffect } from 'react';
import Navbar from '../Components/Navbar';
import Main from '../Components/Main';
import Banner from '../Components/Banner';

function Home(props) {
  const [theme, setTheme] = useState('dark');
  const [searchTerm, setSearchTerm] = useState(''); // Store search input
  const [filteredProducts, setFilteredProducts] = useState(props.HomeProducts || []);

  const HomeProductData = props.HomeProducts;

  const toggleTheme = () => {
    setTheme((prevTheme) => (prevTheme === 'light' ? 'dark' : 'light'));
  };

  // Update filtered products when search term or product data changes
  useEffect(() => {
    if (searchTerm) {
      const results = HomeProductData.filter((product) =>
        product.productName.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredProducts(results);
    } else {
      setFilteredProducts(HomeProductData); // Show all products if no search term
    }
  }, [searchTerm, HomeProductData]);

  return (
    <div className={`homeBody ${theme}`}>
      <Navbar 
        theme={theme} 
        toggleTheme={toggleTheme} 
        onSearch={setSearchTerm} // Pass callback to Navbar
      />
      <Banner />
      <Main proData={filteredProducts} /> {/* Render filtered data */}
    </div>
  );
}

export default Home;
