import React, { useState, useEffect } from 'react';
import "../Pages/Admin.css"

function AddProduct({ selectedProduct, isEditing, clearSelectedProduct }) {
    // Set state for product form fields
    const [productName, setproductName] = useState('');
    const [brand, setbrand] = useState('');
    const [category, setcategory] = useState('All');
    const [subCategory, setsubCategory] = useState('');
    const [specification, setspecification] = useState('');
    const [unit, setunit] = useState('pcs');
    const [tags, settags] = useState('');
    const [description, setdescription] = useState('');
    const [price, setprice] = useState('');
    const [compare, setcompare] = useState('');
    const [discount, setdiscount] = useState('');
    const [thumbnail, setthumbnail] = useState('');
    const [imgone, setimgone] = useState('');
    const [imgtwo, setimgtwo] = useState('');
    const [imgthree, setimgthree] = useState('');
    const [rate, setrate] = useState(3.5);
    const [stock, setstock] = useState(1);
    const [sales, setsales] = useState('');
    const [ppv, setppv] = useState('');

    // Populate the form if editing
    useEffect(() => {
        if (isEditing && selectedProduct) {
            setproductName(selectedProduct.name);
            setbrand(selectedProduct.brand);
            setcategory(selectedProduct.category);
            setsubCategory(selectedProduct.subCategory);
            setspecification(selectedProduct.specification);
            setunit(selectedProduct.unit);
            settags(selectedProduct.tags);
            setdescription(selectedProduct.description);
            setprice(selectedProduct.price);
            setcompare(selectedProduct.compare);
            setdiscount(selectedProduct.discount);
            setthumbnail(selectedProduct.thumbnail);
            setimgone(selectedProduct.imgone);
            setimgtwo(selectedProduct.imgtwo);
            setimgthree(selectedProduct.imgthree);
            setrate(selectedProduct.rate);
            setstock(selectedProduct.stock);
            setsales(selectedProduct.sales);
        }
    }, [isEditing, selectedProduct]);

    // Clear the form fields
    function clear() {
        setproductName("");
        setbrand("");
        setcategory("All");
        setsubCategory("");
        setspecification("");
        setunit("pcs");
        settags("");
        setdescription("");
        setprice("");
        setcompare("");
        setdiscount("");
        setthumbnail("");
        setimgone("");
        setimgtwo("");
        setimgthree("");
        setrate(3.5);
        setstock(1);
        setsales("");
        setppv("");
        if (clearSelectedProduct) {
            clearSelectedProduct(); // Reset editing state
        }
    }

    // Handle form submit (both for adding and editing)
    function submit(e) {
        e.preventDefault();
        const productData = {
            name: productName,
            brand,
            category,
            subCategory,
            specification,
            unit,
            tags,
            description,
            price,
            compare,
            discount,
            thumbnail,
            imgone,
            imgtwo,
            imgthree,
            rate,
            stock,
            sales,
        };

        const url = isEditing ? `http://localhost:5006/products/${selectedProduct.id}` : 'http://localhost:5006/products';
        const method = isEditing ? 'PUT' : 'POST';

        if (window.confirm(isEditing ? `Do you really want to update ${productName}?` : `Are you sure you want to add ${productName}?`)) {
            fetch(url, {
                method: method,
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(productData),
            })
                .then((response) => response.json())
                .then((proddata) => {
                    console.log('Success:', proddata);
                    clear(); // Clear form after submission
                })
                .catch((error) => {
                    console.error('Error:', error);
                });
        }
    }

    const subCategories = {
        Electronics: ['Phones', 'Accessories', 'Tablet', 'Laptops', 'Cameras', 'Television'],
        Fashion: ['Men', 'Women', 'Kids'],
        Furniture: ['Chairs', 'Tables', 'Beds', 'Sofas'],
        Appliances: ['Refrigerators', 'Washing Machines', 'Microwaves', 'Air Conditioners'],
        Beauty: ['Makeup', 'Skincare', 'Haircare', 'Fragrances'],
        Food: ['Groceries', 'Snacks', 'Beverages', 'Dairy'],
        Sports: ['Cycles', 'Equipments', 'Addins', 'Accessories'],
    };

    const handleCategoryChange = (event) => {
        setcategory(event.target.value);
    };

    return (
        <>
            <main className="addProductMain">
                <form action="">
                    <table className='addProductsMainTable'>
                        <thead>
                            <tr>
                                <th colSpan="2">
                                    <h1>{isEditing ? "Edit Product Information" : "Add Product Information"}</h1>
                                </th>
                            </tr>
                        </thead>
                        <tbody>
                            {/* Other input fields remain the same */}
                            <tr>
                                <td><label htmlFor="">Product Name</label></td>
                                <td><input type="text" placeholder='Product Name' value={productName} onChange={(e) => setproductName(e.target.value)} /></td>
                            </tr>
                            <tr>
                                <td><label htmlFor="">Brand</label></td>
                                <td><input type="text" placeholder='Brand Name' value={brand} onChange={(e) => setbrand(e.target.value)} /></td>
                            </tr>
                            <tr>
                                <td><label htmlFor="Category">Category</label></td>
                                <td>
                                    <select name="Category" id="Category" value={category} onChange={handleCategoryChange}>
                                        <option value="All">All Category</option>
                                        <option value="Electronics">Electronics</option>
                                        <option value="Fashion">Fashion</option>
                                        <option value="Furniture">Furniture</option>
                                        <option value="Appliances">Appliances</option>
                                        <option value="Beauty">Beauty</option>
                                        <option value="Food">Food</option>
                                        <option value="Sports">Sports</option>
                                    </select>
                                </td>
                            </tr>
                            {/* Other fields... */}
                        </tbody>
                        <tfoot>
                            <tr className='addtablefoot'>
                                <td className='addproduct'>
                                    <button type="submit" onClick={submit} className='addSubmit'>
                                        {isEditing ? "Update Product" : "Add Product"}
                                    </button>
                                </td>
                                <td className='addproduct'>
                                    <button type="submit" onClick={clear} className='clearSubmit'>Clear Data</button>
                                </td>
                            </tr>
                        </tfoot>
                    </table>
                </form>
            </main>
        </>
    );
}

export default AddProduct;
