import React, { useEffect } from 'react'
import { useLocation } from 'react-router-dom';
import "./Product.css"
import StarRating from '../Components/StarRating';
import PrimaryNav from '../Components/PrimaryNav';

function Product() {

    let location = useLocation()
    let Asset = location.state
    // let n = Asset.rate

    useEffect(() => {
        // let emiResult = calemi(Asset.face);
        const currentDate = new Date();
        console.log(currentDate);
        // console.log(Asset, "Assets")
        // console.log(emiResult, "EMI calculation result");
    }, [Asset.price]);

    // function calemi(emicost) {
    //     let cleanedCost = parseFloat(Asset.face.replace(/,/g, ''));

    //     return (cleanedCost / 12).toFixed(2);
    // }

    function formatDate(date) {
        const days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
        const months = ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'];

        const dayName = days[date.getDay()];
        const day = date.getDate();
        const month = months[date.getMonth()];

        return `${dayName}, ${day} ${month}`;
    }

    const currentDate = new Date();
    console.log("Current Date: ", formatDate(currentDate));

    function addDays(date, days) {
        const result = new Date(date);
        result.setDate(result.getDate() + days);
        return result;
    }

    const dateIn3Days = addDays(currentDate, 3);
    console.log("Date after 3 days: ", formatDate(dateIn3Days));

    return (
        // <Navbar />
        <>
            <PrimaryNav />
            <div className="productBody">
                <div className="productMainArea">
                    <div className="productImgArea">
                        <img src={Asset.thumbnail} alt="" className="productImgAreaThumbnail" />
                        <div className="productImgAreaImgSliders">
                            <img src={Asset.thumbnail} alt="" className="productimgSlider1" />
                            <img src={Asset.imgone} alt="" className="productimgSlider2" />
                            <img src={Asset.imgtwo} alt="" className="productimgSlider3" />
                            <img src={Asset.imgthree} alt="" className="productimgSlider4" />
                        </div>
                    </div>
                    <div className="productInfoArea">
                        <div className="productTitleArea">
                            <div className="titleAreaDescription">
                                <h1>{Asset.description}</h1>
                            </div>
                            <div className="productRatingArea">
                                <StarRating rates={Asset.rate} />
                                <p className='card-rate-info'>
                                    ({Asset.rate})
                                </p>
                            </div>
                        </div>
                        <div className="productPriceArea">
                            <h2>
                                <b>₹ {Asset.price}/-</b>
                                <p>{Asset.discount} %</p>
                            </h2>
                            <s>₹ {Asset.compare} </s>
                        </div>
                        <div className="productPurchaseArea">
                            <button className="productPurchasebtn">Buy now</button>
                            button.produc
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}

export default Product
