import React from 'react';
import { useState } from 'react';
import './Register.css';
import { object, string, number, date, InferType } from 'yup';

function Register() {

    let [formData, setformData] = useState({
        firstName: '',
        lastName: '',
        email: '',
        phone: '',
        password: '',
        confirmPassword: '',
    });

    let userSchema = object({
        firstName: string().required('First name is required'),
        firstName: string().required('last name is required'),
        email: string().email('Invalid Email Format').required('Email is required'),
        firstName: string().matches(/^\d{10}$/, 'Must be 10 digits').required('Phone number is required'),
        password: string()
        .required('Password is required')
        .min(8, 'Must be 8 characters')
        .matches(/[!@#$%^&*(),.?":{}|<>]/, 'Must contain one symbol')
      });

    const handleSubmit = (e) => {
        e.preventDeafult();
        console.log(formData, 'form ka data aa gaya bc')
    }
    const handleChange = (e) => {
        const {name, value} = e.target;

        setformData({
            ...formData,
            [name]: value,
        });
    };
    return (
        <>
            <div className="RegisterBody" onSubmit={handleSubmit}>
                <div className="RegisterPageLogo">
                    <img src="../images/ShopinWhiteLogo.png" alt="" />
                </div>
                <div className="registerFormDiv">
                    <div className="RegisterHeadDiv">
                        <h2>Register</h2>
                        <button className='clearFormbtn'>X</button>
                    </div>
                    <form className="RegisterBodyDiv" onSubmit={handleSubmit}>
                        <label htmlFor="firstName">First Name:</label>
                        <input
                            type="text"
                            name='firstName'
                            value={formData.firstName}
                            placeholder='Enter your First Name...'
                            onChange={handleChange}
                        />

                        <label htmlFor="lastName">Last Name:</label>
                        <input
                            type="text"
                            name='lastName'
                            value={formData.lastName}
                            placeholder='Enter your Last Name...'
                            onChange={handleChange}
                        />

                        <label htmlFor="Email">Email:</label>
                        <input
                            type='email'
                            name='email'
                            value={formData.email}
                            placeholder='Enter your Email id...'
                            onChange={handleChange}
                        />

                        <label htmlFor="Phone">Phone:</label>
                        <input
                            type='number'
                            name='phone'
                            value={formData.phone}
                            placeholder='Enter your Phone Number...'
                            onChange={handleChange}
                         />

                        <label htmlFor="Password">Password:</label>
                        <input
                            type='password'
                            name='password'
                            value={formData.password}
                            placeholder='Enter your Password...'
                            onChange={handleChange}
                        />

                        <label htmlFor="Confirm">Confirm:</label>
                        <input
                            type='password'
                            name='confirmPassword'
                            placeholder='Re-enter your Password...'
                            onChange={handleChange}
                        />

                        <button className="registerFormBtn">Submit</button>
                        <p className='navToLogin'>Already a user? then <a href="#">Login</a> Now</p>
                    </form>
                </div>
            </div>
        </>
    )
}

export default Register
