import React from 'react'
import axios from 'axios';
import Navbar from '../Components/Navbar'
import Cartlist from '../Components/Cartlist'
import "./Cart.css"

function Cart(props) {
    let CartData = props.Showlist
    console.log(CartData, "sidebar data")

    const removeFromCart = async (productId) => {
      try {
          const response = await axios.get('http://localhost:5006/users/1');
          const updatedCart = response.data.cartList.filter(id => id !== productId);
          await axios.patch('http://localhost:5006/users/1', { cartList: updatedCart });
          alert('Removed from Cart');
      } catch (error) {
          console.error('Error removing from cart:', error);
      }
  };

  return (
    <>
    <Navbar/>
    <div className="CartBody">
      <div className="cartBodyListBody">
        <h1 className='CartTitleis'>Cart Items</h1>
        {CartData.map((procar)=> (
          <>
          {/* <div className="clickProduct" onClick={()=>{openProduct(procar) }}></div> */}
          <Cartlist
              imgSrc={procar.thumbnail}
              title={procar.productName}
              price={procar.price}
              Qty={procar.stock}
              rate={procar.rate}
              ppv={procar.ppv}
              unit={procar.unit}
              
            />
          </>
        ))}
      </div>
    </div>

    </>
  )
}

export default Cart
