import React, { useState } from 'react';
import './Register.css';
import * as Yup from 'yup';

function Register() {
    const [errors, setErrors] = useState({});
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        email: '',
        phone: '',
        password: '',
        confirmPassword: ''
    });

    const userSchema = Yup.object({
        firstName: Yup.string().required('First name is required'),
        lastName: Yup.string().required('Last name is required'),
        email: Yup.string().email('Invalid email format').required('Email is required'),
        phone: Yup.string().matches(/^\d{10}$/, 'Must be 10 digits').required('Phone number is required'),
        password: Yup.string()
            .required('Password is required')
            .min(8, 'Must be at least 8 characters')
            .matches(/[!@#$%^&*(),.?":{}|<>]/, 'Must contain one symbol')
            .matches(/[0-9]/, 'Must contain at least one number')
            .matches(/[A-Z]/, 'Must contain at least one uppercase')
            .matches(/[a-z]/, 'Must contain at least one lowercase'),
        confirmPassword: Yup.string()
            .oneOf([Yup.ref("password")], 'Passwords must match')
            .required('Confirm password is required'),
    });

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await userSchema.validate(formData, { abortEarly: false });
            console.log('Form is Submitted', formData);
        } catch (error) {
            const newErrors = {};
            error.inner.forEach((err) => {
                newErrors[err.path] = err.message;
            });
            setErrors(newErrors);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const clear = () => {
        setFormData({
            firstName: '',
            lastName: '',
            email: '',
            phone: '',
            password: '',
            confirmPassword: ''
        });
        setErrors({});
    };

    return (
        <div className="RegisterBody">
            <div className="RegisterPageLogo">
                <img src="../images/ShopinWhiteLogo.png" alt="Shopin Logo" />
            </div>
            <div className="registerFormDiv">
                <div className="RegisterHeadDiv">
                    <h2>Register</h2>
                    <button className='clearFormbtn' type='button' onClick={clear}>X</button>
                </div>
                <form className="RegisterBodyDiv" onSubmit={handleSubmit}>
                    {['firstName', 'lastName', 'email', 'phone', 'password', 'confirmPassword'].map((field) => (
                        <div key={field} className="inputWrapper">
                            <label htmlFor={field}>
                                {field.replace(/([A-Z])/g, ' $1')}
                                {['firstName', 'lastName', 'email', 'phone', 'password', 'confirmPassword'].includes(field) && <span className="required">*</span>}
                            </label>
                            <input
                                type={field.includes('password') ? 'password' : field === 'email' ? 'email' : 'text'}
                                name={field}
                                value={formData[field]}
                                placeholder={`Enter your ${field.replace(/([A-Z])/g, ' $1')}...`}
                                onChange={handleChange}
                            />
                            {errors[field] && <div className="error">{errors[field]}</div>}
                        </div>
                    ))}
                    <button className="registerFormBtn" type='submit'>Submit</button>
                    <p className='navToLogin'>Already a user? <a href="#">Login</a> Now</p>
                </form>
            </div>
        </div>
    );
}

export default Register;
