import React from 'react';
import fullStar from './images/fullStar.svg';
import halfStar from './images/halfStar.svg';
import emptyStar from './images/emptyStar.svg';

function StarRating({ rate }) {
    const totalStars = 5; // Total number of stars

    // Calculate the number of full stars
    const fullStars = Math.floor(rate);

    // Check if there's a half star
    const hasHalfStar = rate % 1 !== 0;

    // Calculate how many empty stars
    const emptyStars = totalStars - fullStars - (hasHalfStar ? 1 : 0);

    return (
        <div className="star-rating">
            {/* Render full stars */}
            {[...Array(fullStars)].map((_, index) => (
                <img key={`full-${index}`} src={fullStar} alt="full star" />
            ))}

            {/* Render half star if needed */}
            {hasHalfStar && <img src={halfStar} alt="half star" />}

            {/* Render empty stars */}
            {[...Array(emptyStars)].map((_, index) => (
                <img key={`empty-${index}`} src={emptyStar} alt="empty star" />
            ))}
        </div>
    );
}

export default StarRating;
