import React, { useState, useEffect } from 'react';
import './Register.css';
import * as Yup from 'yup';
import { Link, Outlet, useNavigate } from 'react-router-dom';

function Register() {
    const [loginData, setLoginData] = useState({ loginemail: '', loginpwd: '' });
    const [loginErrors, setLoginErrors] = useState({});
    const [errors, setErrors] = useState({});
    const [create, setCreate] = useState(true);
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        email: '',
        phone: '',
        password: '',
        confirmPassword: ''
    });
    const [showLogo, setShowLogo] = useState(true);
    const navigate = useNavigate();

    const userSchema = Yup.object({
        firstName: Yup.string().required('First name is required'),
        lastName: Yup.string().required('Last name is required'),
        email: Yup.string().email('Invalid Email Format').required('Email is required'),
        phone: Yup.string().matches(/^\d{10}$/, 'Must be 10 digits').required('Phone number is required'),
        password: Yup.string()
            .required('Password is required')
            .min(8, 'Must be 8 characters')
            .matches(/[!@#$%^&*(),.?":{}|<>]/, 'Must contain one symbol')
            .matches(/[0-9]/, 'Must contain at least one number')
            .matches(/[A-Z]/, 'Must contain at least one uppercase')
            .matches(/[a-z]/, 'Must contain at least one lowercase'),
        confirmPassword: Yup.string()
            .oneOf([Yup.ref("password")], 'Passwords must match')
            .required('Confirm password is required'),
    });

    useEffect(() => {
        const timer = setTimeout(() => {
            setShowLogo(false);
        }, 1000);
        return () => clearTimeout(timer);
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            // Validate form data with Yup
            await userSchema.validate(formData, { abortEarly: false });

            // Proceed only if form is valid
            const userData = {
                firstName: formData.firstName,
                lastName: formData.lastName,
                email: formData.email,
                phone: formData.phone,
                password: formData.password,
            };

            const response = await fetch('http://localhost:5006/users', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(userData),
            });

            const result = await response.json();
            if (response.ok) {
                console.log('Registration Successful:', result);
                clear();
                navigate('/');
            } else {
                console.error('Registration Failed:', result);
            }
        } catch (error) {
            const newErrors = {};
            error.inner.forEach((err) => {
                newErrors[err.path] = err.message;
            });
            setErrors(newErrors);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const clear = () => {
        setFormData({
            firstName: '',
            lastName: '',
            email: '',
            phone: '',
            password: '',
            confirmPassword: ''
        });
        setErrors({});
    };

    const handlePage = () => {
        setCreate(!create);
    };

    return (
        <div className="RegisterBody">
            {showLogo ? (
                <div className="logoContainer">
                    <img src="../images/Shopin Logo.png" alt="Logo" className="logoImage" />
                </div>
            ) : (
                <div className="registerFormDiv">
                    <Link to="/" className="RegisterPageLogo">
                        <img src="../images/ShopinWhiteLogo.png" alt="Logo" />
                    </Link>
                    {create ? (
                        <div className="registerFormBody">
                            <div className="RegisterHeadDiv">
                                <h2>Register</h2>
                                <button className="clearFormbtn" type="button" onClick={clear}>X</button>
                            </div>
                            <form className="RegisterBodyDiv" onSubmit={handleSubmit}>
                                <div className="firstnamechild formchild">
                                    <label htmlFor="firstName">First Name:</label>
                                    <input
                                        type="text"
                                        name="firstName"
                                        autoComplete='first name'
                                        value={formData.firstName}
                                        placeholder="Enter your First Name..."
                                        onChange={handleChange}
                                    />
                                    <p className="errorstatement">
                                        {errors.firstName && <div className="error">{errors.firstName}</div>}
                                    </p>
                                </div>

                                <div className="lastnamechild formchild">
                                    <label htmlFor="lastName">Last Name:</label>
                                    <input
                                        type="text"
                                        name="lastName"
                                        autoComplete='last name'
                                        value={formData.lastName}
                                        placeholder="Enter your Last Name..."
                                        onChange={handleChange}
                                    />
                                    <p className="errorstatement">
                                        {errors.lastName && <div className="error">{errors.lastName}</div>}
                                    </p>
                                </div>

                                <div className="emailchild formchild">
                                    <label htmlFor="email">Email:</label>
                                    <input
                                        type="email"
                                        name="email"
                                        autoComplete='email'
                                        value={formData.email}
                                        placeholder="Enter your Email..."
                                        onChange={handleChange}
                                    />
                                    <p className="errorstatement">
                                        {errors.email && <div className="error">{errors.email}</div>}
                                    </p>
                                </div>

                                <div className="phonechild formchild">
                                    <label htmlFor="phone">Phone:</label>
                                    <input
                                        type="text"
                                        name="phone"
                                        autoComplete='phone'
                                        value={formData.phone}
                                        placeholder="Enter your Phone Number..."
                                        onChange={handleChange}
                                    />
                                    <p className="errorstatement">
                                        {errors.phone && <div className="error">{errors.phone}</div>}
                                    </p>
                                </div>

                                <div className="passwordchild formchild">
                                    <label htmlFor="password">Password:</label>
                                    <input
                                        type="password"
                                        name="password"
                                        value={formData.password}
                                        placeholder="Enter your Password..."
                                        onChange={handleChange}
                                    />
                                    <p className="errorstatement">
                                        {errors.password && <div className="error">{errors.password}</div>}
                                    </p>
                                </div>

                                <div className="confirmpwdchild formchild">
                                    <label htmlFor="confirmPassword">Confirm Password:</label>
                                    <input
                                        type="password"
                                        name="confirmPassword"
                                        value={formData.confirmPassword}
                                        placeholder="Re-enter your Password..."
                                        onChange={handleChange}
                                    />
                                    <p className="errorstatement">
                                        {errors.confirmPassword && <div className="error">{errors.confirmPassword}</div>}
                                    </p>
                                </div>
                                <button className="registerFormBtn" type="submit">Create</button>
                            </form>
                        </div>
                    ) : (
                        <div className="loginFormBody">
                            <div className="RegisterHeadDiv">
                                <h2>Login</h2>
                                <button className="clearFormbtn" type="button" onClick={clear}>
                                    X
                                </button>
                            </div>

                            <form className="loginBodyDiv" onSubmit={handleLoginSubmit}>
                                <div className="loginemail loginformchild">
                                    <label htmlFor="loginemail">Email or Phone</label>
                                    <input
                                        type="text"
                                        name="loginemail"
                                        autoComplete='email'
                                        value={loginData.loginemail}
                                        placeholder="Enter Email or Phone Number..."
                                        onChange={handleLoginChange}
                                    />
                                    {loginErrors.loginemail && (
                                        <p className="loginerrorstatement">{loginErrors.loginemail}</p>
                                    )}
                                </div>

                                <div className="loginpassword loginformchild">
                                    <label htmlFor="loginpwd">Password:</label>
                                    <input
                                        type="password"
                                        name="loginpwd"
                                        value={loginData.loginpwd}
                                        placeholder="Enter your Password..."
                                        onChange={handleLoginChange}
                                    />
                                    {loginErrors.loginpwd && (
                                        <p className="loginerrorstatement">{loginErrors.loginpwd}</p>
                                    )}
                                </div>

                                <button className="registerFormBtn loginformbtn" type="submit">
                                    Login
                                </button>
                            </form>
                        </div>
                    )}
                    {/* <Outlet context={{ formData, errors, handleChange, handleSubmit, clear }} /> */}
                    {create ? (
                        <p className="navToLogin">
                            Already a user? <Link to='login' className='navtologinanchor' onClick={handlePage}>Login</Link> now!!
                        </p>
                    ) : (
                        <p className="navToLogin">
                            New to Shopin? <Link to='signup' className='navtologinanchor' onClick={handlePage}>Create Account</Link> now!!
                        </p>
                    )}
                </div>
            )}
        </div>
    );
}

export default Register;
