// src/Pages/Home.js
import React, { useState, useEffect } from 'react';
import Navbar from '../Components/Navbar';
import Main from '../Components/Main';
import Banner from '../Components/Banner';
import { response as fallbackProducts } from '../Data/productData'; // â¬… fallback

function Home() {
  const [theme, setTheme] = useState('dark');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('All');

  const [allProducts, setAllProducts] = useState([]); // ðŸ”„ Live or fallback data
  const [filteredProducts, setFilteredProducts] = useState([]);

  const toggleTheme = () => {
    setTheme((prevTheme) => (prevTheme === 'light' ? 'dark' : 'light'));
  };

  const isFiltered = searchTerm || selectedCategory !== 'All';

  // âœ… Fetch data (live or fallback)
  useEffect(() => {
    fetch('http://localhost:5006/products')
      .then((res) => {
        if (!res.ok) throw new Error('Network response was not ok');
        return res.json();
      })
      .then((data) => {
        setAllProducts(data);
      })
      .catch((error) => {
        console.error('Fetch failed, using fallback:', error);
        setAllProducts(fallbackProducts);
      });
  }, []);

  // âœ… Apply search + category filters
  useEffect(() => {
    let results = allProducts;

    if (selectedCategory !== 'All') {
      results = results.filter((product) => product.category === selectedCategory);
    }

    if (searchTerm) {
      results = results.filter((product) =>
        product.productName.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    setFilteredProducts(results);
  }, [searchTerm, selectedCategory, allProducts]);

  return (
    <div className={`homeBody ${theme}`}>
      <Navbar
        theme={theme}
        toggleTheme={toggleTheme}
        onSearch={setSearchTerm}
        onCategoryChange={setSelectedCategory}
      />
      {!isFiltered && <Banner />}
      <Main proData={filteredProducts} />
    </div>
  );
}

export default Home;
