import React, { useState } from 'react';
import './Register.css';
import * as Yup from 'yup';

function Register() {
    const [errors, setErrors] = useState({});
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        email: '',
        phone: '',
        password: '',
        confirmPassword: ''
    });

    const userSchema = Yup.object({
        firstName: Yup.string().required('First name is required'),
        lastName: Yup.string().required('Last name is required'),
        email: Yup.string().email('Invalid Email Format').required('Email is required'),
        phone: Yup.string().matches(/^\d{10}$/, 'Must be 10 digits').required('Phone number is required'),
        password: Yup.string()
            .required('Password is required')
            .min(8, 'Must be 8 characters')
            .matches(/[!@#$%^&*(),.?":{}|<>]/, 'Must contain one symbol')
            .matches(/[0-9]/, 'Must contain at least one number')
            .matches(/[A-Z]/, 'Must contain at least one uppercase')
            .matches(/[a-z]/, 'Must contain at least one lowercase'),
        confirmPassword: Yup.string()
            .oneOf([Yup.ref("password")], 'Passwords must match')
            .required('Confirm password is required'),
    });

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await userSchema.validate(formData, { abortEarly: false });
            console.log('Form is submitted', formData);
        } catch (error) {
            const newErrors = {};
            error.inner.forEach((err) => {
                newErrors[err.path] = err.message;
            });
            setErrors(newErrors);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const clear = () => {
        setFormData({
            firstName: '',
            lastName: '',
            email: '',
            phone: '',
            password: '',
            confirmPassword: ''
        });
        setErrors({});
    };

    return (
        <div className="RegisterBody">
            <div className="registerFormDiv">
                <div className="RegisterPageLogo">
                    <img src="../images/ShopinWhiteLogo.png" alt="Logo" />
                </div>
                <div className="registerFormBody">
                    <div className="RegisterHeadDiv">
                        <h2>Register</h2>
                        <button className="clearFormbtn" type="button" onClick={clear}>X</button>
                    </div>
                    <form className="RegisterBodyDiv" onSubmit={handleSubmit}>
                        <div className="firstnamechild formchild">
                            <label htmlFor="firstName">First Name:</label>
                            <input
                                type="text"
                                name="firstName"
                                value={formData.firstName}
                                placeholder="Enter your First Name..."
                                onChange={handleChange}
                            />
                            {errors.firstName && <div className="error">{errors.firstName}</div>}
                        </div>

                        <div className="lastnamechild formchild">
                            <label htmlFor="lastName">Last Name:</label>
                            <input
                                type="text"
                                name="lastName"
                                value={formData.lastName}
                                placeholder="Enter your Last Name..."
                                onChange={handleChange}
                            />
                            {errors.lastName && <div className="error">{errors.lastName}</div>}
                        </div>

                        <div className="emailchild formchild">
                            <label htmlFor="email">Email:</label>
                            <input
                                type="email"
                                name="email"
                                value={formData.email}
                                placeholder="Enter your Email..."
                                onChange={handleChange}
                            />
                            {errors.email && <div className="error">{errors.email}</div>}
                        </div>

                        <div className="phonechild formchild">
                            <label htmlFor="phone">Phone:</label>
                            <input
                                type="text"
                                name="phone"
                                value={formData.phone}
                                placeholder="Enter your Phone Number..."
                                onChange={handleChange}
                            />
                            {errors.phone && <div className="error">{errors.phone}</div>}
                        </div>

                        <div className="passwordchild formchild">
                            <label htmlFor="password">Password:</label>
                            <input
                                type="password"
                                name="password"
                                value={formData.password}
                                placeholder="Enter your Password..."
                                onChange={handleChange}
                            />
                            {errors.password && <div className="error">{errors.password}</div>}
                        </div>

                        <div className="confirmpwdchild formchild">
                            <label htmlFor="confirmPassword">Confirm Password:</label>
                            <input
                                type="password"
                                name="confirmPassword"
                                value={formData.confirmPassword}
                                placeholder="Re-enter your Password..."
                                onChange={handleChange}
                            />
                            {errors.confirmPassword && <div className="error">{errors.confirmPassword}</div>}
                        </div>
                    <button className="registerFormBtn" type="submit">Submit</button>
                    </form>

                    <p className="navToLogin">Already a user? <a href="#">Login</a> now</p>
                </div>
            </div>
        </div>
    );
}

export default Register;
