import React, { useState, useEffect } from 'react';
import AdminCharts from '../Components/AdminCharts';
import '../Pages/Admin.css';
import StarRating from '../Components/StarRating';
import { response } from '../Data/productData';

function ProductReview() {
  const [getProductData, setgetProductData] = useState([]);

  useEffect(() => {
    // First, try to fetch from the server
    fetch("http://localhost:5006/products")
      .then((resp) => {
        if (!resp.ok) {
          throw new Error("Failed to fetch from server");
        }
        return resp.json();
      })
      .then((serverData) => {
        setgetProductData(serverData);
        console.log("Fetched from server:", serverData);
      })
      .catch((error) => {
        console.error("Fetch failed, using fallback data:", error);
        // Fallback to local JSON
        setgetProductData(response.products); // ✅ use products array from JSON
      });
  }, []);

  function deleteData(id, productName) {
    if (window.confirm(`Are you sure you want to delete ${productName}?`)) {
      fetch(`http://localhost:5006/products/${id}`, {
        method: "DELETE",
      })
        .then((resp) => {
          if (resp.ok) {
            setgetProductData((prevProducts) =>
              prevProducts.filter((product) => product.id !== id)
            );
            alert(`${productName} has been deleted.`);
          } else {
            console.error("Failed to delete the product");
          }
        })
        .catch((error) => console.error("Error deleting product:", error));
    }
  }

  return (
    <div className="productReviewBody">
      {getProductData.map((prod) => (
        <div className="clickProductChart" key={prod.id}>
          <AdminCharts
            imgSrc={prod.thumbnail}
            title={prod.productName}
            price={prod.price}
            desc={prod.description}
            rate={prod.rate}
            brand={prod.brand}
            market={prod.compare}
            category={prod.category}
            subCategory={prod.subCategory}
            stock={prod.stock}
            sales={prod.sales}
            onButtonClick={() => deleteData(prod.id, prod.productName)}
          />
        </div>
      ))}
    </div>
  );
}

export default ProductReview;
