import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';
import "./Product.css";
import StarRating from '../Components/StarRating';
import PrimaryNav from '../Components/PrimaryNav';
import Main from '../Components/Main';

function Product(props) {
    let HomeProductData = props.HomeProducts;
    let location = useLocation();
    let Asset = location.state;

    const [currentImage, setCurrentImage] = useState(Asset.thumbnail);

    const addToCart = async () => {
        try {
            await axios.patch('http://localhost:5006/users/1', {
                cartList: [...Asset.cartList, Asset.id]
            });
            alert('Added to Cart');
        } catch (error) {
            console.error('Error adding to cart:', error);
        }
    };

    const addToWishlist = async () => {
        try {
            await axios.patch('http://localhost:5006/users/1', {
                wishList: [...Asset.wishList, Asset.id]
            });
            alert('Added to Wishlist');
        } catch (error) {
            console.error('Error adding to wishlist:', error);
        }
    };

    useEffect(() => {
        let emiResult = calemi(Asset.price);
        console.log(emiResult, "EMI calculation result");
    }, [Asset.price]);

    function calemi(emicost) {
        let cleanedCost = parseFloat(Asset.price.replace(/,/g, ''));
        return (cleanedCost / 12).toFixed(2);
    }

    return (
        <>
            <PrimaryNav />
            <div className="productBody">
                <div className="productMainArea">
                    <div className="productInfoArea">
                        <h2>{Asset.description}</h2>
                        <button onClick={addToCart} className="productCartbtn">Add to Cart</button>
                        <button onClick={addToWishlist} className="OrderAddWish">Add to Wishlist</button>
                    </div>
                </div>
                <Main proData={HomeProductData} />
            </div>
        </>
    );
}

export default Product;
