import React, { useState, useEffect } from 'react'
import AdminCharts from '../Components/AdminCharts'
import "../Pages/Admin.css"

function ProductReview(props) {
    let adminProductData = props.adminProducts
    const [getProductData, setgetProductData] = useState([]);

    // function ProductData() {
    //     fetch("http://localhost:5006/products")
    //         .then((resp) => {
    //             resp.json().then((response) => {
    //                 setgetProductData(response);
    //                 console.log(response, "response");
    //             });
    //         });
    // }

    // useEffect(() => {
    //     ProductData();
    //     console.log(ProductData)
    // }, []);

    function deleteData(id, productName) {
        if (window.confirm(`Are you sure you want to delete ${product.productName}?`)) {
            fetch(`http://localhost:5006/products/${id}`, {
                method: "DELETE",
            })
                .then((resp) => {
                    if (resp.ok) {
                        setgetProductData((prevProducts) =>
                            prevProducts.filter((product) => product.id !== id)
                        );
                        alert(`${product.productName} has been deleted.`);
                    } else {
                        console.error("Failed to delete the product");
                    }
                })
                .catch(error => console.error("Error deleting product:", error));
        }
    }

    // function updateData (id, productName)
        // fetch ()    

    return (
        <div className="productReviewBody">
            {adminProductData.map((prod) => (
                <div className="clickProductChart">
                    <AdminCharts
                        imgSrc={prod.thumbnail}
                        title={prod.name}
                        price={prod.price}
                        desc={prod.description}
                        rate={prod.rate}
                        brand={prod.brand}
                        market={prod.compare}
                        category={prod.category}
                        subCategory={prod.subCategory}
                        stock={prod.stock}
                        sales={prod.sales}
                        deleteOnClick={() => deleteData(prod.id, prod.name)}
                        editOnClick={() => deleteData(prod.id, prod.name)}
                    />
                </div >

            ))
            }
        </div>
    )
}

export default ProductReview
