import React, { useState, useEffect } from 'react';

function AddProduct({ selectedProduct, isEditing, onUpdate }) {

    const [productName, setproductName] = useState('');
    const [brand, setbrand] = useState('');
    const [category, setcategory] = useState('All');
    const [subCategory, setsubCategory] = useState('');
    const [specification, setspecification] = useState('');
    const [unit, setunit] = useState('pcs');
    const [tags, settags] = useState('');
    const [description, setdescription] = useState('');
    const [price, setprice] = useState('');
    const [compare, setcompare] = useState('');
    const [discount, setdiscount] = useState('');
    const [thumbnail, setthumbnail] = useState('');
    const [imgone, setimgone] = useState('');
    const [imgtwo, setimgtwo] = useState('');
    const [imgthree, setimgthree] = useState('');
    const [rate, setrate] = useState(3.5);
    const [stock, setstock] = useState(1);
    const [sales, setsales] = useState('');
    const [ppv, setppv] = useState('');

    useEffect(() => {
        if (isEditing && selectedProduct) {
            // Prefill the form with the selected product data when editing
            setproductName(selectedProduct.name);
            setbrand(selectedProduct.brand);
            setcategory(selectedProduct.category);
            setsubCategory(selectedProduct.subCategory);
            setspecification(selectedProduct.specification);
            setunit(selectedProduct.unit);
            settags(selectedProduct.tags);
            setdescription(selectedProduct.description);
            setprice(selectedProduct.price);
            setcompare(selectedProduct.compare);
            setdiscount(selectedProduct.discount);
            setthumbnail(selectedProduct.thumbnail);
            setimgone(selectedProduct.imgone);
            setimgtwo(selectedProduct.imgtwo);
            setimgthree(selectedProduct.imgthree);
            setrate(selectedProduct.rate);
            setstock(selectedProduct.stock);
            setsales(selectedProduct.sales);
        }
    }, [isEditing, selectedProduct]);

    function clear() {
        setproductName('');
        setbrand('');
        setcategory('');
        setsubCategory('');
        setspecification('');
        setunit('pcs');
        settags('');
        setdescription('');
        setprice('');
        setcompare('');
        setdiscount('');
        setthumbnail('');
        setimgone('');
        setimgtwo('');
        setimgthree('');
        setrate(3.5);
        setstock(1);
        setsales('');
    }

    function submit(e) {
        e.preventDefault();

        const productData = { productName, brand, category, subCategory, specification, unit, tags, description, price, compare, discount, thumbnail, imgone, imgtwo, imgthree, rate, stock, sales };

        if (isEditing) {
            // Confirmation alert before updating
            if (window.confirm('Do you really want to edit this product?')) {
                onUpdate(selectedProduct.id, productData);  // Call the update function
            }
        } else {
            // Add a new product
            fetch('http://localhost:5006/products', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(productData),
            })
                .then((response) => response.json())
                .then((proddata) => {
                    console.log('Success:', proddata);
                    clear();
                })
                .catch((error) => {
                    console.error('Error:', error);
                });
        }
    }

    return (
        <main className="addProductMain">
            <form action="">
                {/* Rest of the form elements */}
                <tfoot>
                    <tr className='addtablefoot'>
                        <td className='addproduct'>
                            <button type="submit" onClick={submit} className='addSubmit'>
                                {isEditing ? 'Update Product' : 'Add Product'}
                            </button>
                        </td>
                        <td className='addproduct'>
                            <button type="button" onClick={clear} className='clearSubmit'>Clear Data</button>
                        </td>
                    </tr>
                </tfoot>
            </form>
        </main>
    );
}

export default AddProduct;
